    @Test(timeout = 2000)
    public void slowA() throws Exception {
        FiboA fibo=new FiboA();
        BigInteger res=fibo.slowA(33);
        boolean ok=res.toString().equals("3524578");
        assertTrue("slowA failed", ok);

        res = fibo.slowA(35);
        ok=res.toString().equals("9227465");
        assertTrue("slowA failed", ok);

    }

    @Test
    public void calcA() throws Exception{
        boolean thrown = false;
        FiboA fibo=new FiboA();
        try {
            fibo.calc(47);
        } catch (IndexOutOfBoundsException e) {
            thrown = true;
        }
        assertTrue(thrown);

        try {
            fibo.calc(-56);
        } catch (IndexOutOfBoundsException e) {
            thrown = true;
        }
        assertTrue(thrown);

        assertTrue("calcA 0", fibo.calc(0)==0);
        assertTrue("calcA 1", fibo.calc(1)==1);
        assertTrue("calcA N", fibo.calc(38)==39088169);
        assertTrue("calcA N", fibo.calc(22)==17711);
    }

