        res=fibo.fastB(75365);
        ok = res.toString().equals("");
        assertTrue("fastB failed", ok);

        res = fibo.fastB(435);
        ok = res.toString().equals("3631938672408255859550518752770954506578238315485816563618848933573305722729383091461442690");
        assertTrue("fastB failed",ok);

        res = fibo.fastB(22122);
        ok = res.toString().equals("");
