package by.it.group151004.burbouski.lesson01;

import static org.junit.Assert.assertTrue;

import java.math.BigInteger;

import org.junit.Test;

@SuppressWarnings("all")
public class Lesson1Test {

	@Test(timeout = 2000)
	public void fastB() throws Exception {
		FiboB fibo = new FiboB();
		BigInteger res = fibo.fastB(55555);
		boolean ok = ""
					.equals(res.toString());
		assertTrue("fastB failed 1", ok);

		res = fibo.fastB(100000);
		ok = ""
					.equals(res.toString());
		assertTrue("fastB failed 2", ok);

		res = fibo.fastB(0);
		ok = "0".equals(res.toString());
		assertTrue("fastB failed 3", ok);

		res = fibo.fastB(1);
		ok = "1".equals(res.toString());
		assertTrue("fastB failed 4", ok);

		res = fibo.fastB(20);
		ok = "6765".equals(res.toString());
		assertTrue("slowA failed 5", ok);

		res = fibo.fastB(37);
		ok = "24157817".equals(res.toString());
		assertTrue("slowA failed 6", ok);
	}

	@Test(timeout = 2000)
	public void fasterC() throws Exception {
		FiboC fibo = new FiboC();
		assertTrue("fasterC failed 1", fibo.fasterC(10, 2) == 1L);
		assertTrue("fasterC failed 2", fibo.fasterC(1, 2) == 1L);
		assertTrue("fasterC failed 3", fibo.fasterC(999999999, 321) == 34L);
		assertTrue("fasterC failed 4", fibo.fasterC(1, 100000000) == 1L);
		assertTrue("fasterC failed 5", fibo.fasterC(17, 8) == 5L);
		assertTrue("fasterC failed 6", fibo.fasterC(9, 7) == 6L);
	}

	@Test(timeout = 2000)
	public void slowA() throws Exception {
		FiboA fibo = new FiboA();
		BigInteger res = fibo.slowA(33);
		boolean ok = "3524578".equals(res.toString());
		assertTrue("slowA failed", ok);

		res = fibo.slowA(37);
		ok = "24157817".equals(res.toString());
		assertTrue("slowA failed", ok);

		res = fibo.slowA(0);
		ok = "0".equals(res.toString());
		assertTrue("slowA failed", ok);

		res = fibo.slowA(1);
		ok = "1".equals(res.toString());
		assertTrue("slowA failed", ok);

		res = fibo.slowA(20);
		ok = "6765".equals(res.toString());
		assertTrue("slowA failed", ok);
	}
}